import { createRouter, createWebHistory } from 'vue-router';

// Ваш код для определения маршрутов

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

// Глобальный навигационный хук `afterEach`
router.afterEach((to) => {
  // Если у маршрута есть метаданные с заголовком, установим его в качестве title страницы
  if (to.meta.title) {
    document.title = to.meta.title;
  } else {
    // Если метаданные не определены, установим заголовок по умолчанию
    document.title = 'Мой сайт'; // Здесь можно задать любой заголовок по умолчанию
  }
});

export default router;
